mapper.insert:
		INSERT INTO COMMON_CODE
		VALUES(
		    (select 'S'||to_char(MAX(SUBSTR(common_cd,2))+1)
		    from common_code
		    where common_cd like 'S101%')
		    ,#{nm}
		    ,#{commonCd}
		    ,(SELECT ctgr
		    from common_code
		    where common_cd=#{commonCd}
		    )
		)
	
	    select 'S'||to_char(MAX(SUBSTR(common_cd,2))+1)
	    from common_code
	    where common_cd like #{commonCd}||'%'

