mapper.select:
SELECT ctgr
FROM COMMON_CODE
WHERE common_cd=#{commonCd}

SELECT memb_no
FROM MEMBER
WHERE nick=#{nick}

mapper.update:
UPDATE BOARD
SET inq=inq+1
WHERE board_no=#{boardNo}

UPDATE BOARD
SET rcom=rcom+1
WHERE board_no=#{boardNo}

UPDATE BOARD
SET nrcom=nrcom+1
WHERE board_no=#{boardNo}

UPDATE BOARD
SET ttl=#{ttl}, cntn=#{cntn},drwup_dt=sysdate
WHERE board_no=#{boardNo}

UPDATE COMMENTS
SET rcom=rcom+1
WHERE comm_no=#{commNo}

UPDATE COMMENTS
SET nrcom=nrcom+1
WHERE comm_no=#{commNo}

mapper.select:
SELECT *
FROM RCOM_CONFIRM
WHERE memb_no=#{membNo} AND board_no=#{boardNo}

SELECT *
FROM RCOM_CONFIRM
WHERE memb_no=#{membNo} AND comm_no=#{commNo}
