mapper.select:
SELECT * FROM( SELECT rownum rn ,faq_no,ttl,cntn,category FROM FAQ
WHERE rownum<=(${page}*10)
AND category=#{category}
AND ttl LIKE '%'||#{ttl}||'%'
) WHERE rn > (${page}-1)*10

SELECT COUNT(faq_no) FROM FAQ
AND category=#{category}
AND ttl LIKE '%'||#{ttl}||'%'

mapper.update:
UPDATE QUESTION
SET inq=inq+1
WHERE qst_no=#{qstNo}
