mapper.select:
SELECT ctgr, common_cd
FROM COMMON_CODE
WHERE h_cd = #{commonCd}

mapper.insert:

	SELECT CONCAT('rev_', NVL(MAX(TO_NUMBER(SUBSTR(rev_no, 5))),
	0)+1)
	FROM PRODUCT_REVIEW

insert into product_review ( rev_no
,cntn
,img
,drwup_dt
,maks
,memb_no
,prdt_no
)
values(
#{revNo}
,#{cntn}
,#{img}
,sysdate
,#{maks}
,#{membNo}
,#{prdtNo}
)
