mapper.select:
SELECT * FROM MEMBER
WHERE id = #{id}
AND   pwd = #{pwd}

SELECT * FROM MEMBER
WHERE nm = #{nm}
AND tel = #{tel}

SELECT COUNT(*) 
FROM MEMBER
WHERE nick = #{nick}

SELECT COUNT(*) 
FROM MEMBER
WHERE id = #{id}

mapper.insert:

	SELECT concat('mem-',NVL(MAX(TO_NUMBER(SUBSTR(memb_no, 5))), 0)+1)
  FROM member

INSERT INTO MEMBER(MEMB_NO
										,NM
										,ID
										,PWD
										,NICK
										,EMAIL
										,TEL
										,POINT
										,JOIN_DT)
						VALUES( #{membNo}
										,#{nm}
										,#{id}
										,#{pwd}
										,#{nick}
										,#{email}
										,#{tel}
										,0
										,sysdate)

mapper.update:
UPDATE member
SET 	 pwd = #{pwd},
temp_pwd = #{tempPwd}
WHERE  id = #{id}

mapper.select:
SELECT id, nm, regexp_replace(tel,'[[:punct:]]','') AS tel FROM member
WHERE  nm = #{nm}
AND    tel = #{tel}

mapper.update:
UPDATE address
SET		 zip = #{zip},
			 addr = #{addr},
			 deta_addr = #{detaAddr}
WHERE memb_no = #{membNo}
AND   addr_no = #{addrNo}
