mapper.select:
select t.item_no, c.sc, c.theme, t.nm,t.prc tprc, t.prc - i.cl_prc change ,trunc(((t.prc-i.cl_prc)/i.cl_prc)*100) rate, origin,desct from (select item_no, cl_prc from item_info where to_char(ta_dt,'yyyy-MM-dd') = to_char(sysdate-1,'yyyy-MM-dd') ) i --item_info 전날 주가정보 join ( SELECT nm,i.item_no, MAX(ta_dt) , ta_prc prc,i.origin,i.desct FROM ta_hr t join item i on i.item_no = t.item_no GROUP BY nm,i.item_no,ta_prc,i.origin,i.desct order by item_no ) t --오늘날짜 중 가장최근 거래가 (=현재가) on i.item_no = t.item_no join (select case substr(common_cd,1,2) when 'S1' then '농산물' when 'S2' then '수산물' end sc ,h_ctgr theme , ctgr item from common_code where length(common_cd) =6
and common_cd = #{value}
and ctgr =#{value}
order by common_cd ) c on c.item=t.nm
