mapper.select:
select t.item_no, c.sc, c.theme, t.nm,NVL(t.prc,0) tprc, NVL(t.prc - i.cl_prc,0) change ,trunc(NVL((t.prc-i.cl_prc) / DECODE(i.cl_prc, 0, NULL, i.cl_prc), 0)*100) rate, origin,desct from (select item_no, cl_prc from item_info where to_char(ta_dt,'yyyy-MM-dd') = ( select to_char(max(ta_dt),'yyyy-MM-dd') from item_info ) ) i --item_info 전날 주가정보 join ( select b.item_no , b.nm,prc,origin,desct from( select distinct(a.item_no) item_no,nm,'tah-'||a.tano ta_no,NVL(ta_prc,0) prc from( SELECT nm,i.item_no, MAX(to_number(substr(ta_no,5))) tano FROM (select * from ta_hr where to_char(ta_dt,'yyyy-MM-dd') =( select to_char(max(ta_dt),'yyyy-MM-dd') from ta_hr ) ) t right join item i on i.item_no = t.item_no GROUP BY nm,i.item_no order by to_number(substr(item_no,5)) ) a left join ta_hr t on a.item_no= t.item_no and to_number(substr(t.ta_no,5)) = a.tano order by to_number(substr(item_no,5)) ) b join item i on b.item_no=i.item_no ) t --오늘날짜 중 가장최근 거래가 (=현재가) on i.item_no = t.item_no join (select case substr(common_cd,1,2) when 'S1' then '농산물' when 'S2' then '수산물' end sc ,h_ctgr theme , ctgr item from common_code where length(common_cd) =6
and common_cd = #{value}
and ctgr =#{value}
order by common_cd ) c on c.item=t.nm

select item_no 
from interest_item 
where memb_no = #{membNo} 
